import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-pagination-header',
  templateUrl: './pagination-header.component.html',
  styleUrls: ['./pagination-header.component.css']
})
export class PaginationHeaderComponent {
   test: string="test";


  @Input() totalCount: number = 0;
  @Input() pageSize: number = 0;
  @Input() pageNumber: number = 0;
}





<h1>  {{test}} </h1>

<div>
      <header> show 

    <span *ngIf="this.totalCount>0">  

    <strong>
    {{(this.pageNumber-1)*(this.pageSize-1)}} - 
    {{this.pageNumber* this.pageSize > this.totalCount
        ? this.totalCount
        :this.pageNumber*this.pageSize


    }}


</strong>
<strong> of </strong> <strong> {{this.totalCount}}</strong>  Results 

</span>
<span *ngIf="this.totalCount==0"> This is 0 results for this filter </span>
</header> 
</div>
