import { Component } from '@angular/core';

@Component({
  selector: 'app-pagination-header',
  templateUrl: './pagination-header.component.html',
  styleUrls: ['./pagination-header.component.css']
})
export class PaginationHeaderComponent {

}
<div>
      <header> show 

    <span *ngIf="this.totalCount>0">  

    <strong>
    {{(pageNumber-1)*(pageSize-1)}} - 
    {{pageNumber*pageSize > this.totalCount
        ? this.totalCount
        :pageNumber*pageSize


    }}


</strong>
<strong> of </strong> <strong> {{this.totalCount}}</strong>  Results 

</span>
<span *ngIf="this.totalCount==0"> This is 0 results for this filter </span>
</header> 
</div>
